#include "push_swap.h"

static int push_arr_to_stack(int arr[], int size, t_stack *stack)
{
	int i;
	if (!stack || size <= 0)
		return (0);
	for (i = 0; i < size; i++)
		if (!stack_push(stack, arr[i]))
			return (0);
	return (1);
}

